vec4 quill( vec4 color, float s, vec2 c )
{
    float t = (color.x / color.y / color.z) * s;
    vec4 h;
    for(int i = 0; i < 24; i++)
    {
        float p = t + 0.1 / t;
        float d = sqrt(p*distance(c.xy,c.yx));
        h = vec4(d / p * fwidth(dFdx(p)/dFdy(p)),vec2(inversesqrt(c.x)),1.0);
        t += d * (iMouse.x / iMouse.y);
    }
    return vec4(h.xyz,t);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec4 color1 = vec4(uv,0.5+0.5*sin(iTime),1.0);
    vec4 squish = quill(color1, float(sin(color1.x)), uv);
    vec4 fcs = vec4(squish.x);
    vec4 color2 = vec4(uv,sin(iTime),1.0);
    vec4 wave = quill(color2, fract(sin(iTime)), uv.yx);
    fragColor = mix(mix(wave,vec4(vec2(mat2(uv,uv)),vec2(mat2(uv,uv))),2.0),texture(iChannel0,uv),2.0);
}
