void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy*10.;

    // Time varying pixel color
    vec3 col = 0.3 + 0.5*cos(uv.xxx+vec3(2,0,-2));
    col += 0.3 + 0.3*sin(2.*iTime+uv.xxx+vec3(2,0,4));
    col += 0.5 + 0.5*sin(5.+iTime+uv.xyx+vec3(4,5,2));
    col += 0.5 + 0.7*cos(1.-iTime*uv.xyy+vec3(2,0,4));

    col -= 0.5 + 0.7*sin(2.+iTime*uv.xyy+vec3(2,0,4));



    // Output to screen
    fragColor = vec4(col,1.0);
}
