void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
vec2 uv = fragCoord.xy / iResolution.y-0.5; // :)

    uv.x += iTime*1.1;
    float l1 = (sin(uv.x + 20.3)+uv.y/0.1)*2.5;
    float l2 = (sin(uv.x + .6)+uv.y/0.18)*2.5;
    float l3 = (sin(uv.x + 20.9)+uv.y/0.26)*2.5;

    vec3 col = vec3(l3,l1,l2)*(l2+l1+l3);
    fragColor = vec4(col,1.);
}
