bool ClipLODTransition(vec2 SVPosition, float DitherFactor){
	float RandCos = cos(dot(floor(SVPosition.xy), vec2(347.83451793,3343.28371963)));
	float RandomVal = fract(RandCos * 1000.0);
	lowp float RetVal = (DitherFactor < 0.0) ?
			step( RandomVal, DitherFactor + 1.0 ) :
			step(DitherFactor , RandomVal);
	return (RetVal - .001 < 0.0);


}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uvs = fragCoord.xy/iResolution.xy;
    vec4 iA = texture( iChannel0, uvs );
    vec4 iB = texture( iChannel1, uvs );


    float factorA = (iMouse.x/ iResolution.x);
    float factorB = factorA-1.0;

    bool cA = ClipLODTransition( fragCoord.xy, factorA );
    bool cB = ClipLODTransition( fragCoord.xy, factorB );

	fragColor = cA?vec4(0.0):iA;
    fragColor = cB?fragColor:iB;
}
