void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 m = iMouse;

    vec2 cur = vec2(8.0 / iResolution.x, 200.0 / iResolution.y); 

    vec2 pos = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y); 

    float value = (sin(pos.x * 3.1415 * 2.0 * 20.0 * cur.x + iTime * 10.0) * cur.y + 1.0) * 0.5;

	float lum = value < pos.y ? uv.x : uv.y;

	fragColor = vec4(lum, 0.0, 0.4, lum);
}

