void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float pos = abs(uv.y * 2.0 - 1.0);
    float penetr = 1.1 - 1.1 * uv.x;
    float slope = 1.0 + 20.0 * pow(min(penetr, 1.0) - 1.0, 2.0);
    float interp = min(max(slope * (pos - 0.5) + 0.5, 0.0), 1.0);
    float cur = mod(floor(pos), 2.0);
    float next = abs(1.0 - cur);
    float color = mix(cur, next, interp);
	fragColor = vec4(color);
}
