void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float pixelSize = abs(sin(iTime * 0.2)) * 10. + 1.;
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec2 offset = vec2(pixelSize / iResolution.x, pixelSize / iResolution.y);
    vec2 coord = vec2(offset.x * floor(uv.x / offset.x), offset.y * floor(uv.y / offset.y));

	fragColor = texture(iChannel0, coord);
}
