#define r iResolution.xy
#define zoom 1.5
#define width (9.0 * zoom)
#define height (15.0 * zoom)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 total = vec3(0.0, 0.0, 0.0);
    float offsetX = mod(fragCoord.x, width);
    float offsetY = mod(fragCoord.y, height);
    vec2 pos = vec2(fragCoord.x - offsetX, fragCoord.y - offsetY);
    vec2 uvNew = vec2(0.0, 0.0);
    for (float row = 0.0; row < height; row++) {
        for (float col = 0.0; col < width; col++) {
            uvNew = vec2((pos.x + col) / r.x, (pos.y + row) / r.y);
            total = total + texture(iChannel0, uvNew).rgb;
        }
    }

    total = total / (width * height);

    // Doing testing for ascii mapping
    // float grey = total.x*75. + total.y*155. + total.z*25.;
    // grey = grey / 255.;

    fragColor = vec4(total, 1.0);
}
