void mainImage(out vec4 o, vec2 I)
{
    // I = I / iResolution.y - .5; // suggested by Fabrice: even shorter but loses center
    I = I / iResolution.xy - .5;
    o = texture(iChannel0, .5 + I / sqrt(1. - 4. * dot(I,I)));
}
