vec2 center;
vec2 center2;
const float radius = 250.0;
//const vec4 frontColor = vec4 (0.77, 0.5, 0.44, 1.0);
//const vec4 frontColor2 = vec4 (0.99, 0.1, 0.44, 1.0);
const vec4 frontColor = vec4 (0.75, 0.55, 0.71, 1.0);
const vec4 frontColor2 = vec4 (0.85, 0.65, 0.81, 1.0);
//const vec4 bgColor = vec4 (0.14, 0.16, 0.18, 0.0);
const vec4 bgColor = vec4 (0.48, 0.32, 0.85, 0.0);
vec4 color;
vec4 color2;
vec4 color3;

vec2 focal;
vec2 focal2;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    center = iResolution.xy / 2.0;
    center = iMouse.xy;
    focal = vec2(center.x + 80.0, center.y - 80.0 );
    focal2 = vec2(center.x - 80.0, center.y - 80.0);
    vec2 focal_direction = focal - center;
    vec2 focal_direction2 = focal2 - center;
    vec2 direction = focal - fragCoord.xy;
    vec2 direction2 = focal2 - fragCoord.xy;
    vec2 direction_normal = normalize(vec2(-direction.y, direction.x));
    vec2 direction_normal2 = normalize(vec2(-direction2.y, direction2.x));
    float center_to_line_dist = abs(dot(focal_direction, direction_normal) / length(direction_normal));
    float center_to_line_dist2 = abs(dot(focal_direction2, direction_normal2) / length(direction_normal2));
    float focal_direction_on_direction_projection_dist = abs(dot(focal_direction, direction) / length(direction));
    float focal_direction_on_direction_projection_dist2 = abs(dot(focal_direction2, direction2) / length(direction2));

    float cut_line_length = sqrt(radius * radius - center_to_line_dist * center_to_line_dist) + (step(dot(direction, focal_direction) / distance(direction , focal_direction) , 0.0) * -2.0 + 1.0 ) * focal_direction_on_direction_projection_dist;
    float cut_line_length2 = sqrt(radius * radius - center_to_line_dist2 * center_to_line_dist2) + (step(dot(direction2, focal_direction2) / distance(direction2 , focal_direction2) , 0.0) * -2.0 + 1.0 ) * focal_direction_on_direction_projection_dist2;
    color = mix(frontColor, bgColor, min(1.0, abs(length(fragCoord.xy - focal))/cut_line_length ) );
    color2 = mix(frontColor2, bgColor, min(1.0, abs(length(fragCoord.xy - focal2))/cut_line_length2 ) );
    color3 = mix(color, color2, 0.5);
    fragColor = color3;
}
