#define UV( p ) texture( iChannel0, p )
#define UV1( p ) texture( iChannel1, p )

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 r = iResolution.xy * iResolution.z;
    vec2 n = fragCoord.xy/r;

    vec3 c = vec3( ceil(1./ r), 0. );

    float Q11 = UV( n - c.xz ).x;
    float Q12 = UV( n + c.xz ).x;
    float Q21 = UV( n - c.zy ).x;
    float Q22 = UV( n + c.zy ).x;

	vec2 grad = vec2( Q12 - Q11, Q21 - Q22 );

    vec4 t = UV1( n );

    t.xy += grad * vec2( c.xy );

	fragColor = t;
}

#define UV( p ) texture( iChannel0, p )

const float ts = 0.016;

/* ADVECTION */

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 r = iResolution.xy * iResolution.z;
    vec2 n = fragCoord / r;

   	vec3 c = vec3( ceil(1./ r), 0. );

    // linear backtrace
    vec2 o = n - ts * r * UV(n).xy;

    // bilinear interpolation
    vec4 Q11 = UV( o + c.xz );
    vec4 Q12 = UV( o + c.zy );
    vec4 Q21 = UV( o - c.xz );
    vec4 Q22 = UV( o - c.zy );

	vec4 P = mix( mix(Q11, Q21, 0.5), mix(Q12, Q22, 0.5), 0.5);

    fragColor = P;
}

#define UV( p ) texture( iChannel0, p )
#define UV1( p ) texture( iChannel1, p )

/* DIFFUSION */

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 r = iResolution.xy * iResolution.z;
    vec2 n = fragCoord / r;
    vec2 m = iMouse.xy / r;
    vec2 md = iMouse.zw / r;

    vec3 c = vec3( ceil(1./r), 0. );

    vec2 n1 = n + c.xz;
    vec2 n2 = n + c.zy;
    vec2 n3 = n - c.xz;
    vec2 n4 = n - c.zy;

    vec4 acc = UV( n1 ) + UV( n2 ) + UV( n3 ) + UV( n4 );

    float ts = 0.016; // timestep 60/fps
    float alpha = pow( n.x, 2. ) / ts;

    vec4 v = UV( n );

    vec4 diff = ( acc + alpha * v ) * ( 1. / ( 4. + alpha ) );

    // adding force
    float rad = 0.05;
    if (max(m.x, m.y) > 0. && distance( m, n ) < rad)
    {
        diff += ( ( pow( length(m), 2. ) / rad ) );
    }

    fragColor = diff;
}

#define UV( p ) texture( iChannel0, p )
#define UV1( p ) texture( iChannel1, p )

const float ts = 0.016;

/* DIVERGENCE */

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 r = iResolution.xy * iResolution.z;
    vec2 n = fragCoord / r;

    vec4 v = UV( n );

    vec3 c = vec3( ceil(1./ r), 0. );

   	float dU = ( UV1(n + c.xz).x - UV1(n - c.xz).x );
    float dV = ( UV1(n + c.zy).y - UV1(n - c.zy).y );

	v -=  vec4( (dU + dV) * (r.x / 2.) );

    fragColor = v;
}
