float speed = 1.0f;

void mainImage(out vec4 color, in vec2 fragCoord) {
    vec2  position = fragCoord - 0.5f * iResolution.xy;
    float radius   = length(position);
    float value    = 0.5f + 0.5f * cos(radius / 7.0f - iTime / speed) / (radius / 7.0f + 1.0f);        

    color = vec4(vec3(value), 1.0f);
}
