float random (in float x) {
    return fract(sin(x)*1e4);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	//fragColor = vec4(uv,0.5+0.5*sin(iTime*20.0)*random(fragCoord.x*fragCoord.y),1.0);
	fragColor = vec4(uv,1.0*random(fragCoord.x*fragCoord.y+iTime*20.0),1.0);
}
