void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    vec3 col = vec3(0.0);
    vec2 p = (-iResolution.xy + 2.0 * fragCoord.xy) / iResolution.y;
    vec2 c = vec2(-0.4, 0.0) + p;
    float nb_iter = 0.0;
    vec2 z  = vec2(0.0);
    const float max_iter = 50.0;
    const float speed = 0.5;

    for(int i = 0; i < int(max_iter); i++) {	
        z = vec2(z.x*z.x - z.y*z.y, 2.0*z.x*z.y) + c;
        if(dot(z,z) > 4.0) break;
        nb_iter += 1.0;
    }

    float t1 = 3.0 * abs(cos(speed * iTime)) * (nb_iter / max_iter);
    float t2 = 3.0 * abs(sin(speed * iTime)) * (nb_iter / max_iter);
    float t3 = max(t1, t2);

    if (nb_iter != max_iter) col = vec3(t1, t2, t3);   
    else col = vec3(0.0);

    fragColor = vec4(col, 1.0);
}
