vec2 center = vec2(0.5,0.5);
float radius = 160.0;
vec3 color = vec3(0.6,0.3,0.4);
float step = 2.0;

vec2 point1 = vec2(0.1,0.9);
vec2 point2 = vec2(0.9,0.1);

vec4 circle(vec2 pos, vec2 center, float radius, vec3 color, float antialias) {
    float d = length(pos - center) - radius;
    //float t = smoothstep(0.0, antialias, d);
    float t = clamp(d, 0.0, 1.0);
    return vec4(color, 1.0 - t);
}

vec4 wire(vec2 pos, vec2 point1, vec2 point2, float width, vec3 color, float antialias) {
    float k = (point1.y - point2.y)/(point1.x - point2.x);
    float b = point1.y - k * point1.x;

    float d = abs(k * pos.x - pos.y + b) / sqrt(k * k + 1.0);
    float t = smoothstep(width/2.0, width/2.0 + antialias, d);
    return vec4(color, 1.0 - t);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pos = fragCoord;
    vec4 layer1 = vec4(1.0,0.65,0.36, 1.0);
    vec4 layer2 = circle(pos,center.xy*iResolution.xy,radius,color,step);

    //add 2 points
    vec4 p1 = circle(pos,point1.xy*iResolution.xy,12.0,color*vec3(2.0, 1.6, 2.1),step);
    vec4 p2 = circle(pos,point2.xy*iResolution.xy,18.0,color*1.8,step);
    vec4 pointShape = mix(p1, p2, p2.a);

    //add new wire connect 2 points
    vec4 w1 = wire(pos, point1.xy*iResolution.xy, point2.xy*iResolution.xy, 8.0, color*0.6, step);
    pointShape = mix(w1, pointShape, pointShape.a);

    layer2 = mix(pointShape,layer2,layer2.a);

    fragColor = mix(layer1,layer2,layer2.a);
}
