void mainImage(out vec4 f, in vec2 g )
{
    vec2 point = g.xy/iResolution.xy;
    vec4 color1 = texture(iChannel0, point);

    vec2 uv = 4.*(2. * g - iResolution.xy)/iResolution.y/2.;

    g /= 45.;

    float rep = color1.r * color1.r * color1.r * 3.;

	float a = 0.; //atan(uv.x, uv.y) / 3.14159 * 0.5;
	float r = length(uv) - iTime*0.3 ;
	a+=r+rep/2.3;
	uv = abs(fract(vec2(a+r,a-r))-0.5);

	float x = uv.x*2.248;
	float y = uv.y*2.;
	float z = uv.y*1.28;
	float hex = abs(max(x*y,x) - 1.386);
	vec3 col = hex * .3 * vec3(0.45,0.3,0.15)/length(uv);

    col = col*col*2.0;


    vec3 hsv = rgb2hsv(col);
    hsv.x += uv.y;
    hsv.y += uv.x;


    col = hsv2rgb(hsv);


    f.rgb =  col;
    if (abs(rep) > 0.85)
        f.rgb = col+.5-vec3(.34,1,.3)/25./rep;


}
