#define ITER 1024

const float zoom = 512.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    vec2 screenPos = -1.0 + 2.0 * fragCoord / iResolution.xy;
    screenPos.x *= iResolution.x / iResolution.y;

    vec2 c = fragCoord / iResolution.xy / zoom + screenPos.xy;
    vec2 z = c;

    for (int i = 0; i < ITER; i++) {
        z = vec2(z.x*z.x - z.y*z.y, 2.0*z.x*z.y) + c;
    }

    fragColor = vec4( dot(z, z) < 4.0 ? 0.0 : 0.95);

}
