// acid post process by mj

// looks cool with movies with checkerboard on the second channel

vec4 calcChannel(float phase)
{
	return texture(iChannel1, vec2(0.5+0.5*sin(phase * 3.145), 0.5));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2	uv = fragCoord.xy / iResolution.xy;
    vec4	color = texture(iChannel0, uv);
    float	brightness = (color.r + color.g + color.b) * 0.3333;
    float	phase1 = mod(brightness + iTime * 0.25, 1.0);
    float	phase2 = mod(brightness + iTime * 0.28, 1.0);
    float	phase3 = mod(brightness + iTime * 0.33, 1.0);
    color.r = calcChannel(phase1).r;
    color.g = calcChannel(phase2).g;
    color.b = calcChannel(phase3).b;
    fragColor = color;
}
