	fragColor = fb;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
		vec2 res = iResolution.xy;

        float step_w = 1.6/res.x;
        float step_h = 1.6/res.y;

        vec2 vUv = fragCoord.xy / res;    	
    	vUv = -1.0 + 2.0 * vUv;
    	vUv *= 0.99;
    	vUv = vUv * 0.5 + 0.5;

        vec2 right = vUv.xy + vec2(step_w, 0.0), 
        left = vUv.xy + vec2(-step_w, 0.0), 
        top = vUv.xy + vec2(0.0, -step_h), 
        bottom = vUv.xy + vec2(0.0, step_h);


    	if(iFrame < 10 || iMouse.z > 0.0){
            fragColor = texture(iChannel1, vUv);
        } else {
            vec2 gradient = vec2(length(texture(iChannel0, right).xyz - texture(iChannel0, left).xyz), length(texture(iChannel0, top).xyz - texture(iChannel0, bottom).xyz));
            fragColor = vec4((length(gradient)) * 1.2);
        	fragColor.rgb -=0.05;
            vec3 last = texture(iChannel0, vUv).rgb;
            fragColor.rgb = clamp( (fragColor.rgb*fragColor.rgb) + (last*(1.0 - fragColor.rgb)) , vec3(0.0), vec3(1.0));

        }

        if(iMouse.z > 0.0){
            vec2 d = fragCoord.xy - iMouse.xy;
       		float m = exp(-length(d) / 10.0);
     		fragColor.rgb += m;
    	}
    }
