void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 screenPos = fragCoord.xy;

    if (distance(screenPos, vec2(100.0, 100.0)) < 10.0) fragColor = vec4(0.5, 0.5, 0.5, 1.0);
    else if (distance(screenPos, vec2(175.0, 100.0)) < 10.0) fragColor = vec4(0.5, 0.5, 0.5, 1.0);
    else if (screenPos.x >= 80.0 && screenPos.x <= 195.0 && 
             screenPos.y <= 120.0 && screenPos.y >= 100.0) fragColor = vec4(1.0, 0.0, 0.0, 1.0);
    else if (screenPos.x >= 120.0 && screenPos.x <= 155.0 && 
             screenPos.y <= 140.0 && screenPos.y >= 120.0) fragColor = vec4(1.0, 0.0, 0.0, 1.0);
    else if (screenPos.x >= 100.0 && screenPos.x <= 120.0 && 
             screenPos.y <= 140.0 && screenPos.y >= 120.0 &&
             (screenPos.y - 120.0) - (screenPos.x - 100.0) < 0.0) fragColor = vec4(1.0, 0.0, 0.0, 1.0);
    else if (screenPos.x >= 155.0 && screenPos.x <= 175.0 && 
             screenPos.y <= 140.0 && screenPos.y >= 120.0 &&
             (screenPos.y - 120.0) - (175.0 - screenPos.x) < 0.0) fragColor = vec4(1.0, 0.0, 0.0, 1.0);
    else fragColor = vec4(0, 0, 0, 1);
}
