float wrap(float x)
{
	return abs(mod(x, 2.)-1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = fragCoord.xy / iResolution.xy;

  	 float size = 0.2;
 		 float cr = pow((p.x - 0.5), 2.) + pow((p.y - 0.5), 2.);
     cr = mod(cr, size);
     cr = abs(cr - size/2.);
  		cr = wrap(cr + iTime/60.);
     vec4 color = texture(iChannel0,  normalize(p - vec2(0.5)) * cr + vec2(0.5));


    fragColor = color;
}
