    //Diagonal shading

    float radiusPreviewPoint = 2.0;
    vec2 beginPoint = vec2(iResolution.x/4.0,iResolution.y/4.0);
    vec2 endPoint = vec2(iMouse.x,iMouse.y);
    vec2 delta = endPoint-beginPoint;

    float dist = length(delta);

    vec4 initColor = vec4(0.6,0.6,0.0,1.0);
    vec4 endColor = vec4(0.0,0.5,0.7,1.0);

    vec4 deltaColor = endColor - initColor;


    float factor = clamp(0.5 * ((pixCoords.x - beginPoint.x)/delta.x + (pixCoords.y-beginPoint.y)/delta.y),0.0,1.0); 

    color= initColor + deltaColor * factor;

    if(sqrt(pow(pixCoords.x - beginPoint.x,2.0)+pow(pixCoords.y - beginPoint.y,2.0))<radiusPreviewPoint){
        color = vec4(1.0,0.0,0.0,0.1);
    }

    if(sqrt(pow(pixCoords.x - endPoint.x,2.0)+pow(pixCoords.y - endPoint.y,2.0))<radiusPreviewPoint){
        color = vec4(1.0,0.0,0.0,0.1);
    }

}
