void mainImage(out vec4 color, in vec2 pixCoords)
{
    float barsNumber = 18.0;

    float levelCenter = texture(iChannel0,vec2(1.0,0.0)).y ;
    float levelBar = texture(iChannel0,vec2(1.0 / barsNumber,0.0)).y ;
    // Background
    color = vec4(0.1, 0.1, 0.1, 1.0);

    float radius = iResolution.x / 15.0;

    // Centre du Cercle
    float lSunCenterX = 0.5*iResolution.x;
    float lSunCenterY = 0.5*iResolution.y;

    float PI = 3.14;
    float radToDeg = 180.0 / PI;
    float lTan = sqrt(pow(lSunCenterX - pixCoords.x, 2.0) + pow(lSunCenterY - pixCoords.y, 2.0));
    float lDeltaX = lSunCenterX - pixCoords.x;
    float lDeltaY = lSunCenterY - pixCoords.y;
    float lAngle = atan(lDeltaX, lDeltaY) * radToDeg;
    float lDeltaRay = 30.0;

    if (lTan < radius * levelBar)
    {
       	color = vec4(max(1.0-(lTan / radius * levelBar),0.2), 0.1, 0.0, 1.0);
        color.xyz * levelCenter;
    }
    else if ((mod(lAngle,20.0) <= 10.0) && (lTan <levelBar * 400.0)  && (mod(lSunCenterX - lTan,25.0) < 15.0))
    {
        color = vec4(0.8 * lTan / iResolution.y, 0.0, 0.0, 1.0);
    }
}
