float expStep( float x, float k, float n ){
    return exp( -k*pow(x,n) );
}

float parabola( float x, float k ){
    return pow( 4.0*x*(1.0-x), k );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord.xy/iResolution.xy;
    //st.x *= u_resolution.x/u_resolution.y;

    float arg = st.y;

    vec3 pct = vec3(0.);

    vec3 colorB = vec3(0.912,0.236,0.571);
	vec3 colorA = vec3(1.000,0.853,0.224);

    vec3 grad = mix(colorA, colorB, st.y+.1);



    pct.z = expStep(arg, 5., 6.0);
	pct.z +=  expStep(arg, 5., 3.0);
	//pct.z = clamp(pct.z,0.,1.);

    float freq = sin(st.x+iTime/20.)*10.;
    float f = sin(freq*st.x+iTime)/25.+.3;
	float c = smoothstep(f-.2, f+.2, st.y);


    float freq2 = sin(st.x+iTime/22.)*10.;
    float f2 = sin(freq2*st.x+iTime)/23.+.3;
	float c2 = smoothstep(f2-.2, f2+.2, 1.-st.y);

    c*= c2;
    pct.z*=c;

    vec3 color = vec3(0.000,0.000,0.000);
    color = mix(color, grad, pct.z);

    fragColor = vec4(color,1.0);
}
