void mainImage( out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord/iResolution.xy;


    vec3 col = (
        vec3(
            (0.5-abs(uv.x-0.5))+(0.5-abs(uv.y-0.5))*(cos(iTime*10.0)+1.0),
            (0.5-abs(uv.x-0.5))+(0.5-abs(uv.y-0.5))*(cos(iTime*20.0)+1.0),
            (0.5-abs(uv.x-0.5))+(0.5-abs(uv.y-0.5))*(cos(iTime*30.0)+1.0)
        )

    );

    // Output to screen
    fragColor = vec4(col,1.0);
}
