// variant of https://shadertoy.com/view/ltdfWH

void mainImage(out vec4 O, vec2 U) {
  for( float L = 20.,k,s, i=0.;
       i++ < L;
       O +=   ( 1. - cos(6.3*k/L) ) / s
	        * ( -1.2 + 8.*texture(iChannel0, (U+U-iResolution.xy)*s/4e4 + i/L ) )
     )	s = exp2( k = mod( i-iTime, L ) ); }
