#define PI 3.141592654

float atan201(float x, float y) {
    return (atan(x, y) + PI) / (PI * 2.);
}

float sin01(float n) {
    return sin(n)/2.+.5;
}

float random (in vec2 _st) {
    return fract(sin(dot(_st.xy,
                    vec2(12.9898,78.233)))*
        			43758.5453123);
}


float noise(float s) {	
    float i = floor(s);
    float f = fract(s);
    float n = mix(random(vec2(i, 0.)), 
                  random(vec2(i+1., -0.568)), 
                  smoothstep(0., 1., f)); 

    return n;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {


    vec2 st = (fragCoord - .5 * iResolution.xy) / iResolution.y;

    st *= 0.376;
    st += sin(st *-20.216).x * length(st)  * (sin(iTime) + 1.) * 20.192 * pow(length(st), .08) * 0.049;

    float f = 1.0 - length(st);
    float seed = abs(sin(atan(st.x, st.y) * 1.040) );
    f += noise(seed*5.752) * 0.656 ;
    float a = f;
    a = smoothstep(0.780, 1.608, f);
    a += smoothstep(0.780, 0.072, f) * 1.376;
    a = pow(a, 0.976);


    vec3 color = vec3(a);

    fragColor = vec4(color,1.0);
}
