void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 st = fragCoord.xy / iResolution.xy;
    st.x *= iResolution.x/iResolution.y;
	st.x -= .45;

    /////////////////////////////////////
    /* MASK */
    vec2 center = vec2(.5, .5);
    float dist = distance(center, st);

    // Cool up and down movement
    //dist -= sin(u_time+st.y*10.)/20.;


    float size =  sin(iTime*2.)/8.+.5;

    float offset = (sin(iTime+10.*atan(st.x, st.y))) / (size * 40.);
	dist += offset;


    //Hard edge
    //dist = dist < .2 ? 0. : 1.;

    // Soft edge
    float radius = sin(iTime)/20.+0.;
    float softness = 0.315 + smoothstep(radius+.1, radius+.02, dist);
    dist = smoothstep(radius,radius+softness, dist);



    /////////////////////////////////////
    /* COLOR */
    vec3 colA = vec3(0.915,0.045,0.037);
    vec3 colB = vec3(0.980,0.691,0.036);
	vec3 colC = vec3(sin(iTime)/8.+.8,0.233,0.975);

    vec3 gradient = 2.0 * mix(colA, colB, smoothstep(0., 1., sin(iTime*3.) * st.y + .1));
    gradient =  1.2 * mix(gradient, colC, smoothstep(0.,1., st.x));

	vec3 color = vec3(gradient);
    color *= (1.-dist);
    fragColor = vec4(color,1.0);
}
