float map(vec3 p) {
    p += tan(p * 10. + iTime) * 0.01;

	float rep = 0.5 + mod(iTime * 0.5, 1.) * 0.5;
    p = mod(p, rep);
    p -= rep * 0.5;

    float r = length(p) - 0.35 + fract(sin(iTime) * 0.15);

    return r;
}

void mainImage( out vec4 o, in vec2 p ) {
    p /= iResolution.xy;
    p -= 0.5;
    p.x *= iResolution.x / iResolution.y;

    p.x += 0.25;
    p.y += 0.15;

    vec3 cam = vec3(0., 0., iTime * 0.25);
    vec3 ray = vec3(p, sin(iTime * 0.01));

    float a = iTime * 0.5;
    mat2 rot = mat2(-sin(a), cos(a),
                     cos(a), sin(a));
    cam.xy *= rot;
    ray.xy *= rot;
    cam.zy = mix(cam.zy, cam.zy * rot, 0.05);
    ray.zy = mix(ray.zy, ray.zy * rot, 0.05);

    float dist = 0.;    
    for (int i=0; i<15; i++) {
        vec3 p = cam + ray * dist;
		float tmp = map(p);

        if (tmp < 0.0001) {
        	break;
        }

        dist += tmp;
    }

    {
        vec3 p = cam + ray * dist;

        o = vec4(smoothstep(0.6, 0.9, fract(dist)), smoothstep(0.4, 0.3, fract(dist)), smoothstep(0.8, 1., dist), 0.) - map(cam - p) * 0.15;
    }
}
