#define PI	3.14159265359
#define PI2	PI*2.0

mat2 rotate(float a)
{
	return mat2(cos(a), sin(a), -sin(a), cos(a));	
}

vec3 hsv(float h, float s, float v)
{
  return mix( vec3( 1.0 ), clamp( ( abs( fract(
    h + vec3( 3.0, 2.0, 1.0 ) / 3.0 ) * 6.0 - 3.0 ) - 1.0 ), 0.0, 1.0 ), s ) * v;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (2.0 * fragCoord.xy - iResolution.xy) / iResolution.y;
    vec2 q = p;
    float time = iTime * 0.5;
	p.y += 0.15 * sin(p.x * 5.0+ time)+ 0.1 * sin(p.x * 1.5+ time);
    p.x += 0.2 * pow(sin(p.y * 3.5 + time), 3.0);
    float n = PI2 / 120.0;
    p *= rotate(floor(atan(-p.x, p.y) / n) * n);
    p.x = abs(p.x);
    p *= 3.0;
    p.y -= 2.0;
    vec3 col = vec3(0.1);
    col = mix(col, texture(iChannel0, q * 0.2).rgb, 0.1);
    col = mix(col, hsv(0.15, 0.7, 1.0)* clamp(0.0, 1.0, -p.y * 0.8), smoothstep(0.003, 0.0, p.y)); 
    col = mix(col, hsv(0.3, 0.7, 1.0), smoothstep(0.5, 0.0, abs(p.y)));
    col = mix(col, hsv(0.1, 0.9, 0.7), smoothstep(0.02, 0.01, max(abs(p.y) - 0.3, abs(p.x))));
    col = mix(col, hsv(0.6, 0.4, 0.8), smoothstep(0.15, 0.0, abs(p.y)));
    p.y += 1.8;
	col = mix(col, hsv(0.0, 0.8, 1.0), smoothstep(0.003, 0.0, p.y)); 
    p.y = abs(p.y) - 0.05;
    col = mix(col, hsv(0.95, 0.8, 0.7), smoothstep(0.08, 0.0, abs(p.y)));
   	fragColor = vec4(col, 1.0);
}
