#define TAU 6.28
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 tex0 = texture(iChannel0,uv);

    float freq1 = 10.3;
    float freq2 = 22.2;
    float freq3 = 10.7;

    //uv*=2.0;
   // uv-= 1.0;
   // uv = abs(uv)-iTime*.2;
    vec4 tex1 = texture(iChannel1,uv);
	fragColor =  ((cos(abs(tex1-tex0)*TAU*freq1)+1.0)/2.0)* ((cos(abs(tex1-tex0)*TAU*freq2)+1.0)/2.0)*((sin(abs(tex1-tex0)*TAU*freq3)+1.0)/2.0);
}
