void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy /iResolution.xy;
    vec4 britney = texture(iChannel0, uv);
    vec4 checkers = texture(iChannel3, uv);
    vec4 grunge = texture(iChannel1, uv);
    vec4 channel2 = texture(iChannel2, uv);
    float mask = .4;
    float checkermask = .2;

    vec4 color;
    if (britney.g > mask && britney.r < mask && britney.b < mask) {
        color = grunge;
    } else {
        color = britney;
    }

    color.b =sin(iTime * 9.0); //add flashing to blue channel

    color = mix(color,channel2,sin(iTime));
    color = color + (iDate * .1); //makes it all red n' orangey, I dunno.

    //make dem chackers move
    checkermask = sin(iTime * 2.0) + 1.0;

    /*Mask britters with the checker pattern*/
     if (checkers.r > checkermask && checkers.g > checkermask  && checkers.b > checkermask ) {
        color = mix(grunge, color, .3);
    } else {
        color = color;
    } 

 	color = ceil(color - 0.5);

    if (color.r >= .9 && color.g >= .9 && color.b >= .9) {
        color = grunge;
    }


    fragColor = color;
}
