/* Compiled with Shaderforth: https://github.com/daeken/Shaderforth
:globals
	@vec3 uniform =iResolution
	@float uniform =iTime
;

:m time iTime ;

iResolution frag->position =p

:m c* ( $a $b ) [ a .x b .x * a .y b .y * - a .y b .x * a .x b .y * + ] ;
:m csq dup c* ;
:m cmag length ;

6 =>maxplunge

[
	0 [ -.746 .09997 ]
	1 [ .275 .0077 ]
	2 [ -.101 .837 ]
	[ -.088 .6555 ]
] !size 1 + =>count time maxplunge count * mod maxplunge 2 * / floor choose =point

maxplunge time maxplunge 2 * mod maxplunge swap - abs - =mtime
[ 0 0 ] =z
p mtime mtime 1 + ** / point + =c

200 =>numiters

{
	float numiters 1 - / =depth
	z csq c + =z
	&break z cmag 2 > when
} numiters times

	[ depth 1 1 ] hsv1->rgb
	[ 0 0 0 ]
depth 1 < select ->fragcolor
*/

vec3 hsv1_rgb(vec3 hsv) {
	return mix(vec3(1., 1., 1.), clamp(abs(mod(hsv.x * 6. + vec3(0., 4., 2.), 6.) - 3.) - 1., 0., 1.), hsv.y) * hsv.z;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	float depth;
	vec2 p = (fragCoord.xy / iResolution.xy * 2. - 1.) * vec2(iResolution.x / iResolution.y, 1.);
	vec2 point = floor(mod(iTime, 48.) / 12.) == 0. ? vec2(-.746, .09997) : floor(mod(iTime, 48.) / 12.) == 1. ? vec2(.275, .0077) : floor(mod(iTime, 48.) / 12.) == 2. ? vec2(-.101, .837) : vec2(-.088, .6555);
	float mtime = 6. - abs(6. - mod(iTime, 12.));
	vec2 z = vec2(0., 0.);
	vec2 c = p / pow(mtime, mtime + 1.) + point;
	for(int temp_4 = 0; temp_4 < 200; ++temp_4) {
		depth = float(temp_4) / 199.;
		z = vec2(z.x * z.x - z.y * z.y, z.y * z.x + z.x * z.y) + c;
		if(length(z) > 2.) {
			break;
		}
	}
	fragColor = vec4(depth < 1. ? hsv1_rgb(vec3(depth, 1., 1.)) : vec3(0., 0., 0.), 1.);
}
