float rad (float deg) {
    return deg * 0.01745329;
}

vec2 rot (vec2 coord, vec2 centre, float angle) {
    mat2 rm = mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
    return (coord - centre) * rm + centre;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragCoord = rot(fragCoord, iMouse.xy, distance(fragCoord, iMouse.xy) / 100. + iTime / 100. - cos(iTime * 2. + distance(fragCoord, iMouse.xy) / 200.));

	vec2 uv = fragCoord / iResolution.xy;
    uv.y *= 6.;
    uv.y -= 3.;

    uv = rot(uv, vec2(0.5, 0.5), -distance(uv, iMouse.xy / iResolution.xy) / 10.);

    vec2 sinmv = vec2(sin(iTime * 10.) * 10., cos(iTime * 10.) * 10.);
    vec2 wavy = vec2(sin(fragCoord.x / 3. + iTime * 20.) * (10. + sin(iTime)), cos(fragCoord.y / 4. + iTime * 20.) * (10. - sin(iTime)));

    if (distance(iMouse.xy, fragCoord + sinmv - wavy) < 100.) uv /= 2.;
    else if (distance(iMouse.xy, fragCoord - sinmv + wavy) < 150.) uv *= 2.;

    float sx = uv.x * 6.28318530718 + iTime * 5.;

    float target =  (sin(sx * 1. + rad(90.)) * 1.);
    float target2 = (cos(sx * 1. - rad(90.)) * 1.);

    vec4 color = vec4(uv,0.5+0.5*sin(iTime),1.0);
    vec4 col2 = vec4(0.5+0.5*cos(iTime), -uv, 1.0);
    vec4 col3 = vec4(uv.x, 0.5-0.5*sin(iTime), -uv.y, 1.0);

	fragColor = color;
    if (uv.y < target) fragColor = mix(fragColor, col2, 0.5);
    if (uv.y > target2) fragColor = mix(fragColor, col3, 0.5);

    if (distance(iMouse.xy, fragCoord + wavy) < 50.) fragColor *= 2.;
    if (mod(distance(iMouse.xy + vec2(sin(fragCoord.x / 10.) * 20., cos(fragCoord.x / 10. * 20.)), fragCoord - wavy) + iTime * 50., 100.) > 20. * cos(iTime * 5.) + 40.) fragColor = 1. - fragColor;

    fragColor.r *= 3. * sin(iTime / 3.);
    fragColor.g *= 3. * cos(iTime / 2.);
    fragColor.b *= 3. * tan(iTime / 5.);
}
