#define TAU 6.283
#define rez 16

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pos = (fragCoord.xy - iResolution.xy / 2.0) / min(iResolution.x, iResolution.y) * 2.0;
    float amp = 0.0;
    for(int i=0; i<rez; i++){
        amp += texture(iChannel1, vec2(float(i)/float(rez), 0)).r;
    }
    amp /= float(rez);
    if(length(pos)>1.0){
        fragColor = vec4(vec3(amp),1.0);
    }else{
        vec2 drtpos = normalize(pos)*(tan(length(pos)*TAU/4.0*0.75));
        vec2 trfpos = drtpos * (amp+0.1) * 10.0;
	    fragColor = texture(iChannel0, trfpos);
    }
}
