//steps
#define STEPS 16

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (-iResolution.xy+2.0*fragCoord.xy)/iResolution.y;

    //mouse position
	vec2 c = (-iResolution.xy + 2.0*iMouse.xy)/iResolution.y;

    //center of the circle
    vec2 o = vec2(0.0);
    //radius of the circle
    float r = 0.5;

    //the circle part
    float p = smoothstep(0.0, 1.0, length(uv- o)/r);

    //shape control
    float t = 0.1;
    float r1 = 0.25 * r;

    //the deformed part
    float r0 = r;
    vec2 p0 = o;
    float s0 = 1.0;
    for (int i=1; i<=STEPS; i++) {
        p0 = mix(p0, c, t);
        r0 = mix(r0, r1, t);
        r0 = mix(r0, r1, t);
        s0 = mix(s0, 1.0 / float(STEPS), t);
        p -= s0 * (1.0 - smoothstep(0.0, 1.0, length(uv-p0)/r0));
    }

    float a = p > 0.95 ? 1.0 - smoothstep(0.95, 1.0, p) : smoothstep(0.0, 0.95 ,p);
    a *= 0.75;
    vec4 color = vec4(1.0, 1.0, 1.0, a);

	vec3 bg = texture(iChannel0, uv).xyz;
	fragColor = vec4(color.xyz * color.a + bg.xyz * (1.0 - color.a), 1);
    //fragColor = vec4(smoothstep(0.0, 1.0, p), smoothstep(1.0, 2.0, p), 0.0, 1.0);
}
