const float blurSize = 1.0/512.0;
const float intensity = 0.35;
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec4 sum = vec4(0);
    vec2 texcoord = fragCoord.xy/iResolution.xy;

    sum += texture(iChannel0, vec2(texcoord.x, texcoord.y)) * 0.20;

    sum += texture(iChannel0, vec2(texcoord.x + blurSize, texcoord.y)) * 0.11;
    sum += texture(iChannel0, vec2(texcoord.x - blurSize, texcoord.y)) * 0.11;
    sum += texture(iChannel0, vec2(texcoord.x, texcoord.y + blurSize)) * 0.11;
    sum += texture(iChannel0, vec2(texcoord.x, texcoord.y - blurSize)) * 0.11;

    sum += texture(iChannel0, vec2(texcoord.x + blurSize, texcoord.y + blurSize)) * 0.07;
    sum += texture(iChannel0, vec2(texcoord.x + blurSize, texcoord.y - blurSize)) * 0.07;
    sum += texture(iChannel0, vec2(texcoord.x - blurSize, texcoord.y + blurSize)) * 0.07;
    sum += texture(iChannel0, vec2(texcoord.x - blurSize, texcoord.y - blurSize)) * 0.07;

    sum += texture(iChannel0, vec2(texcoord.x + 2.0*blurSize, texcoord.y)) * 0.02;
    sum += texture(iChannel0, vec2(texcoord.x - 2.0*blurSize, texcoord.y)) * 0.02;
    sum += texture(iChannel0, vec2(texcoord.x, texcoord.y + 2.0*blurSize)) * 0.02;
    sum += texture(iChannel0, vec2(texcoord.x, texcoord.y - 2.0*blurSize)) * 0.02;

    //increase blur with intensity!
    //fragColor = sum*intensity + texture(iChannel0, texcoord); 
    if(sin(iTime) > 0.0)
        fragColor = sum * sin(iTime) + texture(iChannel0, texcoord);
    else
        fragColor = sum * -sin(iTime) + texture(iChannel0, texcoord);
}
