vec3 hsl2rgb(in float h, float s, float l) {
  vec3 rgb = clamp( abs(mod(h*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0);
  return l + s * (rgb-0.5)*(1.0-abs(2.0*l-1.0));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  float t = mod(iTime, 2.0) + 20.0 * 
      (0.3 * sin(floor(iTime/2.0)/0.3)
       - 0.3 * sin(floor(iTime/4.0)*2.0));
  vec2 uv = fragCoord.xy / iResolution.xy;
  float x = uv.x, y = uv.y;
  float h = sin(t*x/20.0) * sin(t*y/20.0) + (4.0-x*sin(y)*y*sin(t));
  float s = sin(t * x * y);
  float l = mod(sin(y*x + (sin(x * t)) - t*y) + sin((x * y) + t), 1.0) - 0.5;
  fragColor = vec4(hsl2rgb(h, s, 0.7*x - l), 1.0);
}
