vec2 uv; // imitate the textureCorrdinate of your foliage mesh
vec2 displacedTexCoord2D(float angle, vec2 pan,vec2 texCoord) {
    return texCoord * mat2(cos(angle), -sin(angle), sin(angle),  cos(angle) ) + pan;
}

float generator(float seed, float pan) {
	vec2 rCoord = displacedTexCoord2D(3.141592 / (mix(8.0 , 16., uv.y)),vec2(pan,0), uv.xy);
	float x = (sin(rCoord.s * seed) - cos(rCoord.s * (seed - 10.))) / 2.7 + tan(rCoord.t * 0.70);
    //return smoothstep(.95, 1., x); // smoothed edge (can't work with masked materials in UE4)
    return floor(clamp(x,0.0,1.0));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    uv = fragCoord.xy / iResolution.xy;
    uv.y = 1.0 - uv.y;
	float a = generator(45., 0.30);
	float b = generator(50., 0.55);
	vec3 colora = a * vec3(.0,.8,.0);
	vec3 colorb = b * vec3(.0,.5,.0);
	fragColor = vec4(mix(colora, colorb, b), 1.0);
}
