void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
vec2 uv = fragCoord.xy / iResolution.xy;

//Question 4
uv.x *= abs(sin(iTime));
uv.y *= abs(sin(iTime));
vec4 myTexture = texture(iChannel0, uv);
fragColor = myTexture;


//Bonus!!!
vec4 myColor =vec4(0.0,1.0,1.0,1.0);
myColor.rgb += sin(uv.x * 100.0);
fragColor = myColor;

}
