#define ITER	120

vec2 map_pixel(in vec2 pix)
{
	float aspect = iResolution.x / iResolution.y;
	return vec2(2.5 * aspect, 2.5) * (pix - vec2(0.5, 0.5));
}


float mbrot_dist_point(in vec2 c, in vec2 p)
{
	vec2 z = c;
	float mindist_sq = 8192.0;

	for(int i=0; i<ITER; i++) {
		float x = (z.x * z.x - z.y * z.y) + c.x;
		float y = (z.x * z.y + z.x * z.y) + c.y;

		z = vec2(x, y);
		vec2 dir = z - p;
		mindist_sq = min(mindist_sq, dot(dir, dir)); 
	}

	return sqrt(mindist_sq);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	float aspect = iResolution.x / iResolution.y;
	vec2 tc = fragCoord.xy / iResolution.xy;

	float t = iTime * 0.5;

	vec2 c;
	if(iMouse.x < 3.0) {
		c = vec2(cos(t) + sin(t * 3.0) * 0.5, sin(t)) * 0.75;
	} else {
		c = map_pixel(iMouse.xy / iResolution.xy);
	}

	vec2 z = map_pixel(tc);

	float m = 1.0 - mbrot_dist_point(z, c);

	fragColor.xyz = vec3(m, m, m);
	fragColor.w = 1.0;
}
