float rand(float t)
{
    return fract(sin(t*8415.281)*3810.3821);
}

float rand2(vec2 st)
{
    return fract(sin(dot(st, vec2(17.1831, 48.218)))*8471.831);
}

float noise(vec2 st)
{
    vec2 i = floor(st);
    vec2 f = fract(st);

    float a = rand2(i);
    float b = rand2(i + vec2(1.0, 0.0));
    float c = rand2(i + vec2(0.0, 1.0));
    float d = rand2(i + vec2(1.0, 1.0));

    vec2 u = smoothstep(0.0, 1.0, f);

    return (a * (1.0-u.x) * (1.0-u.y) + 
            b * u.x * (1.0 - u.y) + 
            c * (1.0-u.x) * u.y + 
            d * u.x * u.y);
}

mat2 rotate2d(float a)
{
    return mat2(cos(a), -sin(a),
                sin(a), cos(a));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord.xy/iResolution.xy;
    st = 2.0 * (st - 0.5);
    st.x *= iResolution.x/iResolution.y;

    st *= 3.0;
    st.x = abs(st.x) - 1.0*iTime;
    float d = 0.35;

    float r = noise(st * rotate2d(-0.01*iTime));
    float c = smoothstep(d, d+0.02, r);
    fragColor = vec4(vec3(c), 1.0);
}
