#define ITERATIONS 5.

#define ZOOM 1.

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord.xy / iResolution.x;
    float col = 0.0;
    uv.x -= 0.5;
    uv.y -= iResolution.y/iResolution.x/2.0;

    uv *= ZOOM;

    if(iMouse.z > 0.0) {
    	vec2 mv = iMouse.xy/iResolution.x;
    	mv.x -= 0.5;
    	mv.y -= iResolution.y/iResolution.x/2.0;
   		uv -= mv*ZOOM;
    }

    vec2 branch = vec2(0);
    vec2 relative;
    float dir;
    for(float i = 1.0; i <= ITERATIONS; i++) {
    	relative = uv-branch;
        relative *= relative;
        dir = atan(relative.y,relative.x);
        dir *= 3.;
        dir -= iTime / 3.;
    	relative = vec2(cos(dir), sin(dir));
    	branch += relative/10./distance(uv, branch)*i;
    }
    float dist = distance(uv, branch);
    col = dist/ITERATIONS/2.0;

	fragColor = vec4(vec3(col),1.0);
}
