const float PI = 3.141569;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 texturespace_uv = fragCoord.xy / iResolution.xy;

    float aperture = 210.0;
  	float apertureHalf = 0.5 * aperture * (PI / 180.0);
  	float maxFactor = sin(apertureHalf);

  	vec2 uv;
  	vec2 xy = 2.0 * texturespace_uv.xy - 1.0;
  	float d = length(xy);
  	if (d < (2.0-maxFactor))
  	{
    	d = length(xy * maxFactor);
    	float z = 2.0 * sqrt(1.0 - d * d);
    	float r = atan(d, z) / PI;
    	float phi = atan(xy.y, xy.x);

    	uv.x = r * cos(phi) + 0.5;
    	uv.y = r * sin(phi) + 0.5;

        // WARP IT A LITTLE BIT
        uv.s += 0.02 * sin(4.0 * iTime + 15.0 * uv.t);
    }
  	else
  	{
    	uv = texturespace_uv.xy;
  	}



  	vec4 c = texture(iChannel0, uv);
  	fragColor = c;
}
