void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float rows = 5.0;
    float cols = 9.0;
    int intx;
    int inty;
    float temp;
    vec4 texture1 = vec4(1.0,1.0,1.0,1.0);
    vec4 texture2 = vec4(0.0,0.0,0.0,1.0);
	vec2 uv = fragCoord.xy / iResolution.xy;

    //Multiplys rows and temp by 2 (still don't know why this is needed, but it works)
    temp = rows;
    rows = temp * 2.0;
    temp = cols;
    cols = temp * 2.0;

    //Multiplys the uv by cols & rows, then converts into an int so modulus can be used
    temp = (uv.x * cols);
    intx = int(temp * 0.5);
    temp = (uv.y * rows);
    inty = int(temp * 0.5);

    //Starts logic for checkerboard

    //If bolth x & y are even do texture 1
    if (intx % 2 == 0 && inty % 2 == 0)
        fragColor = texture1;

    //If bolth x & y are odd do texture 1
    if (intx % 2 != 0 && inty % 2 != 0)
        fragColor = texture1;

    //If x is odd and y is even, do texture 2
    if (intx % 2 != 0 && inty % 2 == 0)
        fragColor = texture2;

    //If y is odd and x is even, do texture 2
    if (intx % 2 == 0 && inty % 2 != 0)
        fragColor = texture2;


}
