float snoise(vec3 uv, float res)
{
	const vec3 s = vec3(1e0, 1e1, 1e3);

	uv *= res;

	vec3 uv0 = floor(mod(uv, res))*s;
	vec3 uv1 = floor(mod(uv+vec3(1.), res))*s;

	vec3 f = fract(uv); f = f*f*(3.0-2.0*f);

	vec4 v = vec4(uv0.x+uv0.y+uv0.z, uv1.x+uv0.y+uv0.z,
		      	  uv0.x+uv1.y+uv0.z, uv1.x+uv1.y+uv0.z);

	vec4 r = fract(sin(v*1e-1)*1e3);
	float r0 = mix(mix(r.x, r.y, f.x), mix(r.z, r.w, f.x), f.y);

	r = fract(sin((v + uv1.z - uv0.z)*1e-1)*1e3);
	float r1 = mix(mix(r.x, r.y, f.x), mix(r.z, r.w, f.x), f.y);

	return mix(r0, r1, f.z)*2.-.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) 
{
    float speed = .3;
    float noise = .3;
    vec2 uv     = fragCoord.xy / iResolution.xy;
	vec2 p = -.5 + uv;
	p.x *= iResolution.x/iResolution.y;

    float fft  = pow(texture(iChannel0, vec2(0.) ).x, 3.)*1.5;

	float color = 3.0 - length(5.*p);

	vec3 coord = vec3(atan(p.x,p.y)/6.283, length(p)*.2, .5);

	for(int i = 1; i <= 7; i++)
	{
		float power = pow(2.0, float(i));
		color += (1.5 / power) * snoise(coord + vec3(0.,-iTime*speed*noise, iTime*speed*noise), power*8.) * noise * fft;
	}

    float opacity = pow(max(color,.3),4.)*(.5+0.4*fft);

    vec4 frontColor = vec4(1.0, 1.0, 1.0, opacity);
	vec3 backColor = vec3(.59, .57, .53);

	fragColor = vec4(mix(backColor, frontColor.rgb, frontColor.a), 1.0);
}
