void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float f[25];
    f[0] = 1.0;    f[1] = 0.0;    f[2] = -1.0;
    f[3] = 2.0;    f[4] = 0.0;    f[5] = -2.0;
    f[6] = 1.0;    f[7] = 0.0;    f[8] = -1.0;

    vec2 tex = 1.0 / vec2(2000.0);

	vec2 uv = fragCoord.xy / iResolution.xy;

    vec3 final = vec3(0.0);

    final += texture(iChannel0, uv+tex*vec2(0.0, 0.0)).rgb * f[4];
    final += texture(iChannel0, uv+tex*vec2(0.0, 1.0)).rgb * f[1];
    final += texture(iChannel0, uv+tex*vec2(0.0, -1.0)).rgb * f[7];
    final += texture(iChannel0, uv+tex*vec2(1.0, 0.0)).rgb * f[5];
    final += texture(iChannel0, uv+tex*vec2(-1.0, 0.0)).rgb * f[3];
    final += texture(iChannel0, uv+tex*vec2(-1.0, -1.0)).rgb * f[0];
    final += texture(iChannel0, uv+tex*vec2(1.0, -1.0)).rgb * f[2];
    final += texture(iChannel0, uv+tex*vec2(1.0, 1.0)).rgb * f[8];
    final += texture(iChannel0, uv+tex*vec2(-1.0, 1.0)).rgb * f[6];

    final = 1.0 - vec3(dot(final, vec3(0.333)));

    float s = sin(iTime*2.5);
    float c = cos(iTime*2.0);
    if ((mat2(s,c,c,-s)*(uv-0.5)).x > 0.0)
    final = texture(iChannel0, uv).rgb * final;
    fragColor = vec4(final,1.0);

}
