float escape(vec2 pos)
{
    vec2 z = vec2(0.0, 0.0);
    vec2 c = pos;
    for (float i = 0.0; i < 256.0; i += 1.0) {
    	vec2 n;
        n.x = z.x * z.x - z.y * z.y;
        n.y = 2.0 * z.x * z.y;
        z = n + c;
        if (dot(z,z) > 4.0) {
        	return i / 256.0;
        }
    }
    return 0.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv.x -= 0.75;
    uv.y -= 0.5;
    uv *= 2.0;
    float f = escape(uv);
    fragColor = vec4(f, f, f, 1.0);
}
