void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    float stripe1 = step(0.0 + ((sin(iTime) + 1.) * 0.45), uv.x);
    stripe1 -= step(0.1 + ((sin(iTime) + 1.) * .45)  ,uv.x);

    stripe1 -= step(0.6, uv.y);
    stripe1 -= 1.-step( 0.4, uv.y);

    fragColor = vec4(stripe1,.5,.5,.5);
}
