float myAngle(vec2 a, vec2 b){
  return atan(b.x - a.x, b.y - a.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	float mx = max(iResolution.x, iResolution.y);
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec2 center = vec2(0.5,0.5);
	float t = iTime * 10.0;
    float angle = myAngle(uv, center)*2.0;
    float dist = distance(uv, center)*300.0;
    float spiral = sin(t - angle - dist);  
    if(spiral > 0.0){
      uv.y += cos(iTime*1.0)/2.0;
      uv.x += sin(iTime*1.0)/2.0;
    	fragColor = vec4(spiral, uv*spiral, 1.0);
  }
}
